mapper.select:
SELECT id, board_id, spot_id, user_id, title, content, author, created_at, updated_at
FROM post
WHERE id = #{id}

mapper.update:
UPDATE post
SET board_id = #{boardId}, spot_id = #{spotId}, user_id = #{userId}, title = #{title}, content = #{content}, author = #{author}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
