mapper.select:
SELECT * FROM spot WHERE true

    AND title LIKE CONCAT('%', #{title}, '%')


    AND tag LIKE CONCAT('%', #{tag}, '%')


    AND type = #{type}


    AND reg1 = #{reg1}


    AND reg2 = #{reg2}

LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM spot
WHERE true

    AND title LIKE CONCAT('%', #{title}, '%')


    AND tag LIKE CONCAT('%', #{tag}, '%')


    AND type = #{type}


    AND reg1 = #{reg1}


    AND reg2 = #{reg2}

SELECT *, (SELECT COUNT(*) FROM spot) as count
FROM spot
LIMIT #{size} OFFSET #{offset}

SELECT *
FROM spot
WHERE SUBSTRING(img_path, 52, 2) = #{month}
  and type = #{type};
